#function for t-test
optab<-function (n1=NULL,n2=NULL,d=NULL,T1T2cratio=1,HaHopratio=1,type = c("two.sample", "one.sample", "paired"),tails = c("two.tailed","one.tailed")) {
  beta.t.test<-function (n1 = NULL, n2 = NULL, d = NULL, sig.level = 0.05, type = c("two.sample", "one.sample", "paired"),tails = c("two.tailed","one.tailed")) {
    if (!is.null(sig.level) && !is.numeric(sig.level) || any(0 > sig.level | sig.level > 1)) 
      stop(sQuote("sig.level"), " must be numeric in [0, 1]")
    if (!is.null(n1) && n1 < 2) 
      stop("number of observations in the first group must be at least 2")
    type <- match.arg(type)
    tails <- match.arg(tails)
    d<-abs(d)
    tsample <- switch(type, one.sample = 1, two.sample = 2, paired = 1)
    tside <- switch(tails, one.tailed = 1, two.tailed = 2)
    if (tside == 1) {
      p.body <- quote({
        nu <- switch(type, one.sample = n1-1, two.sample = n1 + n2 - 2, paired = n1-1)
        pt(qt(sig.level/tside, nu, lower = FALSE), nu, ncp = d * switch(type, one.sample = sqrt(n1), two.sample = (1/sqrt(1/n1 + 1/n2)), paired = sqrt(n1)), lower = FALSE)
      })
    }
    if (tside == 2) {
      p.body <- quote({
        nu <- switch(type, one.sample = n1-1, two.sample = n1 + n2 - 2, paired = n1-1)
        qu <- qt(sig.level/tside, nu, lower = FALSE)
        pt(qu, nu, ncp = d * switch(type, one.sample = sqrt(n1), two.sample = (1/sqrt(1/n1 + 1/n2)), paired = sqrt(n1)), lower = FALSE) + 
          pt(-qu, nu, ncp = d * switch(type, one.sample = sqrt(n1), two.sample = (1/sqrt(1/n1 + 1/n2)), paired = sqrt(n1)), lower = TRUE)
      })
    }
    1-eval(p.body)
  }
  
  w.average.error<-function (alpha=NULL,n1=NULL,n2=NULL,d=NULL,T1T2cratio=1,HaHopratio=1,type = c("two.sample", "one.sample", "paired"),tails = c("two.tailed","one.tailed")) 
    ((alpha*T1T2cratio+HaHopratio*(beta.t.test(n1=n1,n2=n2,d=d,sig.level=alpha,type=type,tails=tails))))/(HaHopratio+T1T2cratio)
  
  min.average.error<-function (n1=NULL,n2=NULL,d=NULL,T1T2cratio=1,HaHopratio=1,type = c("two.sample", "one.sample", "paired"),tails = c("two.tailed","one.tailed")) 
    unlist(optimize(w.average.error,c(0,1),tol=0.0000000000001,n1=n1,n2=n2,d=d,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio,type=type,tails=tails))[2]
  
  alpha<-function (n1=NULL,n2=NULL,d=NULL,T1T2cratio=1,HaHopratio=1,type = c("two.sample", "one.sample", "paired"),tails = c("two.tailed","one.tailed")) 
    unlist(optimize(w.average.error,c(0,1),tol=0.000000000001,n1=n1,n2=n2,d=d,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio,type=type,tails=tails))[1]
  
  beta<-function (n1=NULL,n2=NULL,d=NULL,T1T2cratio=1,HaHopratio=1,type = c("two.sample", "one.sample", "paired"),tails = c("two.tailed","one.tailed")) 
    ((T1T2cratio+HaHopratio)*min.average.error(n1=n1,n2=n2,d=d,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio,type=type,tails=tails)-T1T2cratio*alpha(n1=n1,n2=n2,d=d,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio,type=type,tails=tails))/HaHopratio
  
  list(
    "test type"=match.arg(type),
    "tails"=match.arg(tails),
    "output"=data.frame(
      "sample size 1"=n1,
      "sample size 2"=n2,
      "Cohen's d effect size"=d,
      "Type I/II error cost ratio"=T1T2cratio,
      "Ha/Ho prior probability ratio"=HaHopratio,
      "overall probability of error"=(alpha(n1=n1,n2=n2,d=d,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio,type=type,tails=tails)+HaHopratio*beta(n1=n1,n2=n2,d=d,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio,type=type,tails=tails))/(1+HaHopratio),
      "cost-weighted probability of error"=min.average.error(n1=n1,n2=n2,d=d,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio,type=type,tails=tails),  
      "optimal alpha"=alpha(n1=n1,n2=n2,d=d,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio,type=type,tails=tails),
      "optimal beta"=beta(n1=n1,n2=n2,d=d,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio,type=type,tails=tails),row.names="values"))
  
}

#function for regression
optab.r<-function (n=NULL,r=NULL,T1T2cratio=NULL,HaHopratio=NULL){
  beta.r.test<- function (n = NULL, r = NULL, sig.level = 0.05, power = NULL) {
    if (!is.null(sig.level) && !is.numeric(sig.level) || any(0 > 
                                                             sig.level | sig.level > 1)) 
      stop(sQuote("sig.level"), " must be numeric in [0, 1]")
    if (!is.null(n) && n < 4) 
      stop("number of observations must be at least 4")
    r <- abs(r)
    p.body <- quote({
      ttt <- qt(sig.level/2, df = n - 2, lower = FALSE)
      rc <- sqrt(ttt^2/(ttt^2 + n - 2))
      zr <- atanh(r) + r/(2 * (n - 1))
      zrc <- atanh(rc) + rc/(2 * (n - 1))
      pnorm((zr - zrc) * sqrt(n - 3)) + pnorm((-zr - zrc) * 
                                                sqrt(n - 3))
    })
    #approximate correlation power calculation (arctangh transformation)
    1-eval(p.body)
  }
  
  w.average.error.r<-function (alpha=NULL,n=NULL,r=NULL,T1T2cratio=1,HaHopratio=1) 
    ((alpha*T1T2cratio+HaHopratio*(beta.r.test(n=n,r=r,sig.level=alpha))))/(HaHopratio+T1T2cratio)
  
  min.average.error.r<-function (n=NULL,r=NULL,T1T2cratio=1,HaHopratio=1) 
    unlist(optimize(w.average.error.r,c(0,1),tol=0.000000000001,n=n,r=r,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))[2]
  
  alpha.r<-function (n=NULL,r=NULL,T1T2cratio=1,HaHopratio=1) 
    unlist(optimize(w.average.error.r,c(0,1),tol=0.000000000001,n=n,r=r,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))[1]
  
  beta.r<-function (n=NULL,r=NULL,T1T2cratio=1,HaHopratio=1) 
    ((T1T2cratio+HaHopratio)*min.average.error.r(n=n,r=r,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio)-T1T2cratio*alpha.r(n=n,r=r,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))/HaHopratio
  
  list("output"=data.frame(
    "sample size"=n,
    "correlation coefficient effect size"= r,
    "Type I/II error cost ratio"= T1T2cratio,
    "Ha/Ho prior probability ratio"= HaHopratio, 
    "overall probability of error"= (alpha.r(n=n,r=r,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio)+HaHopratio*beta.r(n=n,r=r,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))/(1+HaHopratio),
    "cost-weighted probability of error"= min.average.error.r(n=n,r=r,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio),  
    "optimal alpha"= alpha.r(n=n,r=r,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio), 
    "optimal beta"= beta.r(n=n,r=r,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio)
  ))
}

#function for logrank
optab.logrank<-function (HR=NULL,events=NULL,controlprpn=NULL,T1T2cratio=1,HaHopratio=1) {
  beta.logrank.test<-function (HR = NULL, events = NULL, controlprpn = NULL, sig.level = 0.05) {
    if (!is.null(HR) && HR < 0) 
      stop("HR must be positive")
    if (!is.null(events) && events < 1) 
      stop("number of events must be at least 1")
    if (!is.null(sig.level) && !is.numeric(sig.level) || any(0 > sig.level | sig.level > 1)) 
      stop(sQuote("sig.level"), " must be numeric in [0, 1]")
    
    power <- pnorm(sqrt(events*controlprpn*(1-controlprpn)*log(HR)^2)-qnorm(1-sig.level/2))
    
    1-power
  }
  
  w.average.error<-function (alpha=NULL,HR=NULL,events=NULL,controlprpn=NULL,T1T2cratio=1,HaHopratio=1) 
    ((alpha*T1T2cratio+HaHopratio*(beta.logrank.test(HR=HR,events=events,controlprpn=controlprpn,sig.level=alpha))))/(HaHopratio+T1T2cratio)
  
  min.average.error<-function (HR=NULL,events=NULL,controlprpn=NULL,T1T2cratio=1,HaHopratio=1) 
    unlist(optimize(w.average.error,c(0,1),tol=0.0000000000001,HR=HR,events=events,controlprpn=controlprpn,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))[2]
  
  alpha<-function (HR=NULL,events=NULL,controlprpn=NULL,T1T2cratio=1,HaHopratio=1) 
    unlist(optimize(w.average.error,c(0,1),tol=0.000000000001,HR=HR,events=events,controlprpn=controlprpn,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))[1]
  
  beta<-function (HR=NULL,events=NULL,controlprpn=NULL,T1T2cratio=1,HaHopratio=1) 
    ((T1T2cratio+HaHopratio)*min.average.error(HR=HR,events=events,controlprpn=controlprpn,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio)-T1T2cratio*alpha(HR=HR,events=events,controlprpn=controlprpn,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))/HaHopratio
  
  list(
    "output"=data.frame(
      "Hazard ratio"=HR,
      "Number of events"=events,
      "proportion of subjects in control group"=controlprpn,
      "Type I/II error cost ratio"=T1T2cratio,
      "Ha/Ho prior probability ratio"=HaHopratio,
      "overall probability of error"=(alpha(HR=HR,events=events,controlprpn=controlprpn,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio)+HaHopratio*beta(HR=HR,events=events,controlprpn=controlprpn,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))/(1+HaHopratio),
      "cost-weighted probability of error"=min.average.error(HR=HR,events=events,controlprpn=controlprpn,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio),  
      "optimal alpha"=alpha(HR=HR,events=events,controlprpn=controlprpn,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio),
      "optimal beta"=beta(HR=HR,events=events,controlprpn=controlprpn,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio),row.names="values"))
}	

#function for chi-squared
optab.chisq<-function (w=NULL,N=NULL,df=NULL,T1T2cratio=1,HaHopratio=1) {
  beta.chisq.test<-function (w = NULL, N = NULL, df = NULL, sig.level = 0.05) {
    if (!is.null(w) && w < 0) 
      stop("w must be positive")
    if (!is.null(N) && N < 1) 
      stop("number of observations must be at least 1")
    if (!is.null(sig.level) && !is.numeric(sig.level) || any(0 > sig.level | sig.level > 1)) 
      stop(sQuote("sig.level"), " must be numeric in [0, 1]")
    p.body <- quote({
      k <- qchisq(sig.level, df = df, lower = FALSE)
      pchisq(k, df = df, ncp = N * w^2, lower = FALSE)
    })
    1-eval(p.body)
  }
  
  w.average.error<-function (alpha=NULL,w=NULL,N=NULL,df=NULL,T1T2cratio=1,HaHopratio=1) 
    ((alpha*T1T2cratio+HaHopratio*(beta.chisq.test(w=w,N=N,df=df,sig.level=alpha))))/(HaHopratio+T1T2cratio)
  
  min.average.error<-function (w=NULL,N=NULL,df=NULL,T1T2cratio=1,HaHopratio=1) 
    unlist(optimize(w.average.error,c(0,1),tol=0.0000000000001,w=w,N=N,df=df,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))[2]
  
  alpha<-function (w=NULL,N=NULL,df=NULL,T1T2cratio=1,HaHopratio=1) 
    unlist(optimize(w.average.error,c(0,1),tol=0.000000000001,w=w,N=N,df=df,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))[1]
  
  beta<-function (w=NULL,N=NULL,df=NULL,T1T2cratio=1,HaHopratio=1) 
    ((T1T2cratio+HaHopratio)*min.average.error(w=w,N=N,df=df,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio)-T1T2cratio*alpha(w=w,N=N,df=df,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))/HaHopratio
  
  list(
    "output"=data.frame(
      "w critical effect size"=w,
      "Number of observations"=N,
      "degrees of freedom"=df,
      "Type I/II error cost ratio"=T1T2cratio,
      "Ha/Ho prior probability ratio"=HaHopratio,
      "overall probability of error"=(alpha(w=w,N=N,df=df,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio)+HaHopratio*beta(w=w,N=N,df=df,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))/(1+HaHopratio),
      "cost-weighted probability of error"=min.average.error(w=w,N=N,df=df,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio),  
      "optimal alpha"=alpha(w=w,N=N,df=df,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio),
      "optimal beta"=beta(w=w,N=N,df=df,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio),row.names="values"))
  
}

#function for ANOVA
optab.anova<-function (u=NULL,v=NULL,f2=NULL,T1T2cratio=1,HaHopratio=1){
  
  beta.glm.test<-function (u = NULL, v = NULL, f2 = NULL, sig.level = 0.05, power = NULL){
    if (!is.null(f2) && f2 < 0) 
      stop("f2 must be positive")
    if (!is.null(u) && u < 1) 
      stop("degree of freedom u for numerator must be at least 1")
    if (!is.null(v) && v < 1) 
      stop("degree of freedom v for denominator must be at least 1")
    if (!is.null(sig.level) && !is.numeric(sig.level) || any(0 > 
                                                             sig.level | sig.level > 1)) 
      stop(sQuote("sig.level"), " must be numeric in [0, 1]")
    p.body <- quote({
      lambda <- f2 * (u + v + 1)
      pf(qf(sig.level, u, v, lower = FALSE), u, v, lambda,lower = FALSE)
    })
    1-eval(p.body)
  }
  
  w.average.error.glm<-function (alpha=NULL,u=NULL,v=NULL,f2=NULL,T1T2cratio=1,HaHopratio=1) 
    ((alpha*T1T2cratio+(HaHopratio*beta.glm.test(u=u,v=v,f2=f2,sig.level=alpha))))/(HaHopratio+T1T2cratio)
  
  min.average.error.glm<-function (u=NULL,v=NULL,f2=NULL,T1T2cratio=1,HaHopratio=1) 
    unlist(optimize(w.average.error.glm,c(0,1),tol=0.00000000001,u=u,v=v,f2=f2,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))[2]
  
  alpha.glm<-function (u=NULL,v=NULL,f2=NULL,T1T2cratio=1,HaHopratio=1) 
    unlist(optimize(w.average.error.glm,c(0,1),tol=0.000000000001,u=u,v=v,f2=f2,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))[1]
  
  beta.glm<-function (u=NULL,v=NULL,f2=NULL,T1T2cratio=1,HaHopratio=1) 
    ((T1T2cratio+HaHopratio)*min.average.error.glm(u=u,v=v,f2=f2,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio)-T1T2cratio*alpha.glm(u=u,v=v,f2=f2,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))/HaHopratio
  
  list("output"=data.frame(
    "numerator degrees of freedom"=u,
    "denominator degrees of freedom"=v,
    "among vs within group variance ratio effect size"=f2,
    "Type I/II error cost ratio"=T1T2cratio, 
    "Ha/Ho prior probability ratio"=HaHopratio,
    "overall probability of error"= (alpha.glm(u=u,v=v,f2=f2,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio)+HaHopratio*beta.glm(u=u,v=v,f2=f2,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))/(1+HaHopratio),
    "cost-weighted probability of error"= min.average.error.glm(u=u,v=v,f2=f2,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio),  
    "optimal alpha"= alpha.glm(u=u,v=v,f2=f2,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio), 
    "optimal beta"= beta.glm(u=u,v=v,f2=f2,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio)
  ))
}
#fucntion for logistic
optab.logistic<-function (P=NULL, PT=NULL, OPT = NULL, n = NULL, Pro1PT = NULL,T1T2cratio=1,HaHopratio=1) {
  beta.logistic.test<-function (P=NULL, PT=NULL, OPT = NULL, n = NULL, Pro1PT = NULL, sig.level = 0.05) {
    if (!is.null(PT/OPT) && PT/OPT < 0) 
      stop("HR must be positive")
    if (!is.null(n) && n < 1) 
      stop("number of events must be at least 1")
    if (!is.null(sig.level) && !is.numeric(sig.level) || any(0 > sig.level | sig.level > 1)) 
      stop(sQuote("sig.level"), " must be numeric in [0, 1]")
    
    power <- pnorm(sqrt((n*(OPT-PT)^2*Pro1PT*(1-Pro1PT))/(P*(1-P)))-qnorm(1-sig.level/2))
    
    1-power
  }
  
  w.average.error<-function (alpha=NULL,P=NULL, PT=NULL, OPT = NULL, n = NULL, Pro1PT = NULL,T1T2cratio=1,HaHopratio=1) 
    ((alpha*T1T2cratio+HaHopratio*(beta.logistic.test(P=P, PT=PT, OPT = OPT, n = n, Pro1PT = Pro1PT,sig.level=alpha))))/(HaHopratio+T1T2cratio)
  
  min.average.error<-function (P=NULL, PT=NULL, OPT = NULL, n = NULL, Pro1PT = NULL,T1T2cratio=1,HaHopratio=1) 
    unlist(optimize(w.average.error,c(0,1),tol=0.0000000000001,P=P, PT=PT, OPT = OPT, n = n, Pro1PT = Pro1PT,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))[2]
  
  alpha<-function (P=NULL, PT=NULL, OPT = NULL, n = NULL, Pro1PT = NULL,T1T2cratio=1,HaHopratio=1) 
    unlist(optimize(w.average.error,c(0,1),tol=0.000000000001,P=P, PT=PT, OPT = OPT, n = n, Pro1PT = Pro1PT,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))[1]
  
  beta<-function (P=NULL, PT=NULL, OPT = NULL, n = NULL, Pro1PT = NULL,T1T2cratio=1,HaHopratio=1) 
    ((T1T2cratio+HaHopratio)*min.average.error(P=P, PT=PT, OPT = OPT, n = n, Pro1PT = Pro1PT,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio)-T1T2cratio*alpha(P=P, PT=PT, OPT = OPT, n = n, Pro1PT = Pro1PT,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))/HaHopratio
  
  list(
    "output"=data.frame(
      "Odds ratio"=PT/OPT,
      "Sample Size"=n,
      "proportion of subjects in control group"=Pro1PT,
      "Type I/II error cost ratio"=T1T2cratio,
      "Ha/Ho prior probability ratio"=HaHopratio,
      "overall probability of error"=(alpha(P=P, PT=PT, OPT = OPT, n = n, Pro1PT = Pro1PT,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio)+HaHopratio*beta(P=P, PT=PT, OPT = OPT, n = n, Pro1PT = Pro1PT,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio))/(1+HaHopratio),
      "cost-weighted probability of error"=min.average.error(P=P, PT=PT, OPT = OPT, n = n, Pro1PT = Pro1PT,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio),  
      "optimal alpha"=alpha(P=P, PT=PT, OPT = OPT, n = n, Pro1PT = Pro1PT,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio),
      "optimal beta"=beta(P=P, PT=PT, OPT = OPT, n = n, Pro1PT = Pro1PT,T1T2cratio=T1T2cratio,HaHopratio=HaHopratio),row.names="values"))
  
}

#function to print the data contents of list from the function
# after clicking the "three dots" icon
printAfterClick<-function(t = x){
  n<-ncol(t)
  str <- paste("",str_replace_all(colnames(t)[1],"[.]"," "),"=", signif(t[1], digits=6))
  for (i in 2:n)
  {
    str<-paste(str, paste(str_replace_all(colnames(t)[i],"[.]"," "),"=", signif(t[i], digits=6)), sep=" & ")
  }
  str<-str_replace_all(str,"[&]","<br>")
  return(str)
}


