server <- function(input, output, session) {
  
  #this results in closing the "Landing" page and opening the "content"
  # page once the calculator button in the "Landing" page is clicked
  observeEvent(input$`close-landing-page`, {
    updateTabsetPanel(session, "page", "content")
  })
  #this allows to have different tabset panel, based on the 
  # options selected for the test-type
  observeEvent(input$testType, {
    updateTabsetPanel(inputId = "params", selected = input$testType)
  })
  #this allows to have "sample size of group 2" numeric input updated in 
  # the panel if type is "two sample"
  output$n2Input <- renderUI({
    
    if (input$type == 'two.sample') {
      numericInput("n2", "sample size of group 2", value = 10)
    } else {
      return(NULL)
    }
    
  })
  #an appropriate function is used based on the test type selected
  sample <- reactive({
    switch(input$testType,
           ttest = optab(n1=input$n1,n2=input$n2,d=input$d,T1T2cratio=input$T1T2cratio, HaHopratio=input$HaHopratio,type = input$type, tails = input$tail),
           regression = optab.r(n=input$n,r=input$r,T1T2cratio=input$T1T2cratio2,HaHopratio=input$HaHopratio2),
           logrank = optab.logrank(HR=input$HR,events=input$events,controlprpn=input$controlprpn,T1T2cratio=input$T1T2cratio3,HaHopratio=input$HaHopratio3),
           logistic = optab.logistic(P=input$P, PT=input$PT, OPT = input$OPT, n = input$n4, Pro1PT = input$Pro1PT, T1T2cratio=input$T1T2cratio4, HaHopratio=input$HaHopratio4),
           chisquared = optab.chisq(w=input$w,N=input$N,df=input$df,T1T2cratio=input$T1T2cratio5,HaHopratio=input$HaHopratio5),
           ANOVA = optab.anova(u=input$u,v=input$v,f2=input$f2,T1T2cratio=input$T1T2cratio6,HaHopratio=input$HaHopratio6)
    )
  })
  
  
  #after a new function is selected this allows printAfterClick() to react 
  # with the sample output
  strin <- reactive({
    printAfterClick(t = sample()$output)
  })
  

  #renders output text and "three dots" icon with pop-up in variable "code", which  
  # gets displayed in the "Output" tab
  output$code <- renderText({ 
    paste("<h1><b>Optimal alpha: ",signif(sample()$output$optimal.alpha, digits=3)," </b></h1>" 
          ,bsPopover(id = "q3",title=NULL, trigger = "click",content =paste("<p>",strin(),"</p>")), #"</b></h1>"
          label = bsButton("q3", label = "", icon = icon("ellipsis-h"), style = "info", size = "height:50px; width:50px")
          
    )
  })
  
  
  
}
shinyApp(ui=ui, server=server)
